import React,{useState,useEffect} from 'react';
import {FormControl,Select,MenuItem,Card,CardContent} from '@material-ui/core'
import InfoBox from './InfoBox'
import Map from './Map'
import Table from './Table';
import './App.css';


function App() {
  const [states,setstates] = useState([]);
  const [state,set_state] = useState("india");
  const [stateInfo,setStateInfo] = useState({});
  const [tableData,setTableData] = useState([]);



  useEffect(()=>{
     fetch("https://api.covidindiatracker.com/total.json").then((response)=>response.json()).then((data)=>{
       var obj = {
        stateCases:data.confirmed,
        stateRecov:data.recovered,
        stateDeaths:data.deaths,
       }
       setStateInfo(obj)  
     })
  },[])
  useEffect(() => {
    const getStatesData = async () =>{
      await fetch('https://api.covidindiatracker.com/state_data.json').then((response) => response.json()).then((data)=>{
        const states = data.map((state)=>(
          {
            name:state.state,
            value:state.id,
            cases:state.confirmed,
            recovered:state.recovered,
            deaths:state.deaths,
            dist:state.districtData
          }
        ))  
         setstates(states);
    })
  }
  getStatesData()
  },[]);

  const onStateChange = async (event) =>{
    const stateCode = event.target.value;
    set_state(stateCode);
     for(var i=0;i<states.length;i++){
       if(states[i].value===stateCode){
        const obj = {
          stateCases:states[i].cases,
          stateRecov:states[i].recovered,
          stateDeaths:states[i].deaths,
          stateName:states[i].name,
          distInfo:states[i].dist

        }

          setStateInfo(obj)
          // setTableData(stateInfo.distInfo)

       }

     console.log(stateCode)
     console.log(stateInfo.distInfo)
     }



  }
  useEffect(()=>{
    if(state==="india"){
       setTableData(null)
    }
    else{
      for(var i=0;i<states.length;i++){
        if(states[i].value===state){
          setTableData(states[i].dist)   
         } 
        }
    }
  },[states])
  return (
    <div className="app">
      <div className="app__left">
      <div className="app__header">
      <h1>Covid-19 Tracker</h1>
      <FormControl className="app__dropdown">
        <Select variant="outlined" onChange={onStateChange} value={state}>
        <MenuItem  value="india">Select State</MenuItem>
          {
            states.map((state) => {
           return <MenuItem key={state.value} value={state.value}>{state.name}</MenuItem>
            })
          }
        </Select>
      </FormControl>
      </div>
      <div className="app__stats">
       <InfoBox title="CoronaVirus Cases" cases={stateInfo.stateCases} total={stateInfo.stateName == null ? 'India':stateInfo.stateName} />
       <InfoBox title="Recovered" cases={stateInfo.stateRecov} total={stateInfo.stateName == null ? 'India':stateInfo.stateName}/>
       <InfoBox title="Deaths" cases={stateInfo.stateDeaths} total={stateInfo.stateName == null ? 'India':stateInfo.stateName}/>
      </div>
      <Map />
      </div>
      <Card className="app__right">
        <CardContent>
          <h3>Live cases</h3>
          <Table states={tableData} />


        </CardContent>
      </Card>
    </div>
  );
}


export default App
