import React,{useState,useEffect} from 'react';
import {FormControl,Select,MenuItem,Card,CardContent} from '@material-ui/core'
import InfoBox from './InfoBox'
import Map from './Map'
import './App.css';

function App() {
  const [states,setstates] = useState([]);
  const [state,set_state] = useState("india");
  const [stateInfo,setStateInfo] = useState({
          stateCases:states[0].cases,
          stateRecov:states[0].recovered,
          stateDeaths:states[0].deaths,
  });

  const setTheState = async (fd) =>{
    const states = fd.map((state)=>(
      {
        name:state.state,
        value:state.statecode,
        cases:state.confirmed,
        recovered:state.recovered,
        deaths:state.deaths
      }
    ))
     await setstates(states);
  }
  useEffect(() => {
    const getStatesData = async () =>{
      await fetch('https://api.covid19india.org/data.json').then((response) => response.json()).then((data)=>{
         const finData = data.statewise
        // console.log(finData)
        setTheState(finData)


    })
  }
  getStatesData()
  },[]);
  const onStateChange = async (event) =>{
    const stateCode = event.target.value;
    set_state(stateCode);
     for(var i=0;i<states.length;i++){
       if(states[i].value===stateCode){
        const obj = {
          stateCases:states[i].cases,
          stateRecov:states[i].recovered,
          stateDeaths:states[i].deaths,
        }

       await setStateInfo(obj)

       }
     }

  }
  return (
    <div className="app">
      <div className="app__left">
      <div className="app__header">
      <h1>Covid-19 Tracker</h1>
      <FormControl className="app__dropdown">
        <Select variant="outlined" onChange={onStateChange} value={state}>
        <MenuItem  value="india">Select State</MenuItem>
          {
            states.map((state) => {
           return <MenuItem key={state.value} value={state.value}>{state.name}</MenuItem>
            })
          }
        </Select>
      </FormControl>
      </div>
      <div className="app__stats">
       <InfoBox title="CoronaVirus Cases" cases={stateInfo.stateCases} total={2000} />
       <InfoBox title="Recovered" cases={123} total={3000}/>
       <InfoBox title="Deaths" cases={123} total={50}/>

      </div>
      <Map />
      </div>
      <Card className="app__right">
        <CardContent>
          <h3>Live cases</h3>
        </CardContent>
      </Card>
    </div>
  );
}

export default App
