import React,{useState,useEffect} from 'react';
import {FormControl,Select,MenuItem,Card,CardContent} from '@material-ui/core'
import InfoBox from './InfoBox'
import Map from './Map'
import './App.css';

function App() {
  const [states,setstates] = useState([]);
  const [state,set_state] = useState("TT")
  useEffect(() => {
    const getStatesData = async () =>{
      await fetch('https://api.covid19india.org/data.json').then((response) => response.json()).then((data)=>{
        const finData = data.statewise
        console.log(finData)
        const states = finData.map((state)=>(
          {
            name:state.state,
            value:state.statecode
          }
        ))
        setstates(states);
    })
  }
  getStatesData()
  },[]);
  const onStateChange = async (event) =>{
    const stateCode = event.target.value;
    set_state(stateCode);
  }
  return (
    <div className="app">
      <div className="app__left">
      <div className="app__header">
      <h1>Covid-19 Tracker</h1>
      <FormControl className="app__dropdown">
        <Select variant="outlined" onChange={onStateChange} value={state}>
        {/* <MenuItem  value="india">India</MenuItem> */}
          {
            states.map((state) => {
           return <MenuItem key={state.value} value={state.value}>{state.name}</MenuItem>
            })
          }
        </Select>
      </FormControl>
      </div>
      <div className="app__stats">
       <InfoBox title="CoronaVirus Cases" cases={123} total={2000} />
       <InfoBox title="Recovered" cases={123} total={3000}/>
       <InfoBox title="Deaths" cases={123} total={50}/>

      </div>
      <Map />
      </div>
      <Card className="app__right">

      </Card>
    </div>
  );
}

export default App
